mapper.select:
select seq, register, title, content, rdate, udate, username from (
select rownum rn, a.seq, a.register, a.title, a.content, a.rdate, a.udate, b.username from sp_board a left join sp_member b on a.register = b.userid where
rownum <= #{pageNum}*#{pageAmount}
order by seq desc ) where rn > (#{pageNum}-1)*#{pageAmount}
